package com.example.liyudong.mvvm;import android.annotation.SuppressLint;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import androidx.databinding.DataBindingUtil;import androidx.databinding.ViewDataBinding;import androidx.recyclerview.widget.RecyclerView;import com.example.liyudong.mvvm.databinding.ItemArticleBinding;import com.example.liyudong.mvvm.net.bean.PublicArticalListBean;import java.util.List;import static androidx.core.util.Preconditions.checkNotNull;public class ArticleAdatper extends RecyclerView.Adapter<ArticleAdatper.MyViewHolder> {    private List<PublicArticalListBean> beans;    private AppCompatActivity lifecycleOwner;    @SuppressLint("RestrictedApi")    public ArticleAdatper(List<PublicArticalListBean> beans, AppCompatActivity lifecycleOwner){        checkNotNull(beans);        checkNotNull(lifecycleOwner);        this.beans = beans;        this.lifecycleOwner = lifecycleOwner;    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        ViewDataBinding viewDataBinding = DataBindingUtil.inflate(LayoutInflater.from(lifecycleOwner), R.layout.item_article, parent, true);        viewDataBinding.setLifecycleOwner(lifecycleOwner);        viewDataBinding.executePendingBindings();        return new MyViewHolder(viewDataBinding.getRoot().getRootView());    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder holder, int position) {        ItemArticleBinding dataBinding = DataBindingUtil.getBinding(holder.itemView);        dataBinding.setArticlebean(beans.get(position));    }    @Override    public int getItemCount() {        return beans.size();    }    class MyViewHolder extends RecyclerView.ViewHolder {        public MyViewHolder(@NonNull View itemView) {            super(itemView);        }    }}