package com.example.liyudong.mvvm.net;import retrofit2.Retrofit;import retrofit2.adapter.rxjava.RxJavaCallAdapterFactory;import retrofit2.converter.gson.GsonConverterFactory;public class MyRetrofit {    private volatile static MyRetrofit instance;    private Retrofit retrofit;    private NetClient netClient;    public static MyRetrofit getInstance() {        synchronized (MyRetrofit.class){            if(instance == null) {                synchronized (MyRetrofit.class) {                    if(instance == null) {                        instance = new MyRetrofit();                    }                }            }        }        return instance;    }    private MyRetrofit() {        retrofit = new Retrofit.Builder()                .addConverterFactory(GsonConverterFactory.create())                .addCallAdapterFactory(RxJavaCallAdapterFactory.create())                .baseUrl(NetConst.BASE_URL)                .build();        netClient = retrofit.create(NetClient.class);    }    public NetClient getNetClient(){        return netClient;    }}