package com.example.liyudong.mvvm.net.bean;public class BaseBean<T> {    public static final int SUCCESS_CODE = 0;    private T data;    private int errorCode;    private String errorMsg;    public T getData() {        return data;    }    public void setData(T data) {        this.data = data;    }    public int getErrorCode() {        return errorCode;    }    public void setErrorCode(int errorCode) {        this.errorCode = errorCode;    }    public String getErrorMsg() {        return errorMsg;    }    public void setErrorMsg(String errorMsg) {        this.errorMsg = errorMsg;    }}