package com.example.liyudong.mvvm.net.repository;import com.example.liyudong.mvvm.net.MyRetrofit;import com.example.liyudong.mvvm.net.NetClient;import com.example.liyudong.mvvm.net.bean.BaseBean;import com.example.liyudong.mvvm.net.bean.PublicArticalListBean;import io.reactivex.Flowable;import io.reactivex.android.schedulers.AndroidSchedulers;import io.reactivex.schedulers.Schedulers;public class ArticleListRepository {    private NetClient netClient;    public ArticleListRepository(){        netClient = MyRetrofit.getInstance().getNetClient();    }    public Flowable<BaseBean<PublicArticalListBean>> getArticles(){        return netClient.getAllList()                .subscribeOn(Schedulers.io())                .observeOn(AndroidSchedulers.mainThread());    }}