package com.example.liyudong.mvvm.viewmodel;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import androidx.lifecycle.ViewModel;public abstract class BaseMvvmActivity<VM extends ViewModel> extends AppCompatActivity {    protected VM mViewModel;    public abstract int getContentView();    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(getContentView());    }}